class SiteAlbumController extends Controller{
	public function index(){
		$sitealbums = SiteAlbum::paginate(10);
		return view("pages.erp.sitealbum.index",["sitealbums"=>$sitealbums]);
	}
	public function create(){
		return view("pages.erp.sitealbum.create",[]);
	}
	public function store(Request $request){
		//SiteAlbum::create($request->all());
		$sitealbum = new SiteAlbum;
		$sitealbum->name=$request->name;
date_default_timezone_set("Asia/Dhaka");
		$sitealbum->created_at=date('Y-m-d H:i:s');
		$sitealbum->description=$request->description;
		if(isset($request->photo)){
			$sitealbum->photo=$request->photo;
		}

		$sitealbum->save();
		if(isset($request->photo)){
			$imageName=$sitealbum->id.'.'.$request->photo->extension();
			$sitealbum->photo=$imageName;
			$sitealbum->update();
			$request->photo->move(public_path('img'),$imageName);
		}

		return back()->with('success', 'Created Successfully.');
	}
	public function show($id){
		$sitealbum = SiteAlbum::find($id);
		return view("pages.erp.sitealbum.show",["sitealbum"=>$sitealbum]);
	}
