	public function update(Request $request,SiteAlbum $sitealbum){
		//SiteAlbum::update($request->all());
		$sitealbum = SiteAlbum::find($sitealbum->id);
		$sitealbum->name=$request->name;
date_default_timezone_set("Asia/Dhaka");
